import Axios from 'axios';
import React from 'react';

import '../css/main.scss'

class Main extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            artists : []
        }
    }
    componentDidMount(){
        console.log(this.state.artists)
    }

    render(){
        let artists = ''
        if(this.props.found_data != ''){
            artists = JSON.parse(this.props.found_data)
            artists = artists.data.artists.items
            console.log(artists)
        } 
        return (
            <main>
                {artists ? artists.map(
                    (artist, key) => {
                        return(
                            <div class='content_artist'>
                                <img src={ artist.images[2] ? (artist.images[2].url) : ''} alt=""/>
                                <div className="info_artist">
                                    <h3 class='artist'>{artist.name}</h3>
                                    <p>followers {artist.followers.total}</p>
                                    <a href={'https://open.spotify.com/artist/' + artist.id}>Ver artist</a>
                                </div>
                            </div>
                        )
                    }) 
                    : ''}

            </main>
        )

    }
}




export default Main;
