import Axios from 'axios';
import React from 'react';

import '../css/main.scss'

class Main extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            artists : []
        }
    }
    componentDidUpdate(){
        Axios('https://api.spotify.com/v1/playlists/37i9dQZF1DX2apWzyECwyZ/tracks',{
            headers : {
                'Authorization' : 'Bearer ' + this.props.token
            },
            params : {
                playlist_id : '37i9dQZF1DX2apWzyECwyZ',
                limit : 10
            },
            method: 'GET'
        })
        .then(function (response) {
            // handle success
            console.log(response)
        })
        .catch(function (error) {
            // handle success
            console.log(error)
        })
    }
    render(){
        let artists = ''
        if(this.props.found_data != ''){
            artists = JSON.parse(this.props.found_data)
            artists = artists.data.artists.items
            console.log(artists)
        } 
        return (
            <div>
                <main>
                    {artists ? artists.map(
                        (artist, key) => {
                            return(
                                <div class='content_artist'>
                                    <div className="cont_img">
                                        <img className='img-fluid' src={ artist.images[2] ? (artist.images[2].url) : ''} alt=""/>
                                    </div>
                                    <div className="info_artist">
                                        <div>
                                            <h3 class='artist'>{artist.name}</h3>
                                            <p>followers {artist.followers.total}</p>
                                            <a href={'https://open.spotify.com/artist/' + artist.id} className='ver_artist' target='_blank'>Ver artista</a>
                                        </div>
                                        <div className='generos'>
                                            <p>
                                            {artist.genres.map(
                                                (genero,index) => {
                                                    console.log(genero)
                                                    return(
                                                        <span className='genero'>{genero}</span>
                                                    )
                                                }
                                            )}
                                            </p>
                                        </div>
                                    </div>
                                </div>
                            )
                        }) 
                        : ''}

                </main>
                <div className="list">
                </div>
            </div>
        )

    }
}




export default Main;
