import Axios from 'axios';
import React from 'react';

import '../css/main.scss'

class Main extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            artist : '',
            albums : false,
            content_albums : [],
            id : ''
        }
    }
    albums(e){
        console.log(e.currentTarget.id)
        this.setState({
            albums : true,
            id : ''
        })

    }   
    render(){
        let artists = ''
        if(this.props.found_data != ''){
            artists = JSON.parse(this.props.found_data)
            artists = artists.data.artists.items
            console.log(artists)
        }
        if(this.state.albums == true){
            var albums = []
            albums = JSON.parse(this.props.albums)
            albums = albums.data.items
            console.log(albums)
        }
        return (
            <div>
                <main>
                    {artists ? artists.map(
                        (artist, key) => {
                            return(
                                <div class='content_artist' onClick={this.albums.bind(this)} id={artist.name}>
                                    <div className="cont_img">
                                        <img className='img-fluid' src={ artist.images[2] ? (artist.images[2].url) : ''} alt=""/>
                                    </div>
                                    <div className="info_artist">
                                        <div>
                                            <h3 class='artist'>{artist.name}</h3>
                                            <p>followers {artist.followers.total}</p>
                                            <a href={'https://open.spotify.com/artist/' + artist.id} className='ver_artist' target='_blank'>Ver artista</a>
                                        </div>
                                        <div className='generos'>
                                            <p>
                                            {artist.genres.map(
                                                (genero,index) => {
                                                    return(
                                                        <span className='genero'>{genero}</span>
                                                    )
                                                }
                                            )}
                                            </p>
                                        </div>
                                    </div>
                                    {
                                        this.state.albums == true &&  this.state.albums == false ?
                                            albums.map(
                                                album => {
                                                    return (
                                                        <p>{album.name}</p>
                                                    )
                                                }
                                            )
                                        : ''
                                    }
                                </div>
                            )
                        }) 
                    : ''}
                </main>
                <div className="list">

                </div>
            </div>
        )

    }
}




export default Main;
