import Axios from 'axios';
import React from 'react';

import '../css/main.scss'

class Main extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            artist : '',
            albums : false,
            content_albums : [],
            id : '',
            albumnes: ''
        }
    }
    async albums(e){
        console.log(e.currentTarget)
        let temp_state = this
        let artist_id = e.currentTarget.id
        await this.setState({
            albums : true,
            id : artist_id
        })
        console.log(this.state.id)
        let respuesta = await Axios('https://api.spotify.com/v1/artists/' + this.state.id + '/albums',{
            headers : {
                'Authorization' : 'Bearer ' + this.props.token
            },
            params : {
                id : artist_id
            },
            method: 'GET'
        })
        let albums_respuesta = await JSON.stringify(respuesta)
        await this.setState({
            albumnes : albums_respuesta
        })
        console.log(this)

    }
    show_albums(ref_albumnes){
        console.log(ref_albumnes)
        let albums = ref_albumnes
        albums.map(
            album => {
                return (
                    <div className="col-4">
                        <div className="album">
                            <a href={album.external_urls.spotify} target='_blank'>
                                <img src={album.images[1].url} class='img-fluid' alt=""/>
                                <p>{album.name}</p>
                            </a>
                            <p class='tracks'>Tracks : <span>{album.total_tracks}</span></p>
                            <span>{album.release_date}</span>
                        </div>
                    </div>
                )
            }
        )
    }
    render(){
        let artists = ''
        let albums =''
        if(this.props.found_data != ''){
            artists = JSON.parse(this.props.found_data)
            artists = artists.data.artists.items
        }
        return (
            <div>
                <main>
                    {artists ? artists.map(
                        (artist, key) => {
                            return(
                                <div>
                                    <div class='row content_artist' onClick={this.albums.bind(this)} id={artist.id}>
                                        <div className="col-2">
                                            <div className="cont_img">
                                                <img className='img-fluid' src={ artist.images[2] ? (artist.images[2].url) : ''} alt=""/>
                                            </div>
                                        </div>
                                        <div className="col-6">
                                            <div className="info_artist">
                                                <div>
                                                    <h3 class='artist'>{artist.name}</h3>
                                                    <p>followers {artist.followers.total}</p>
                                                    <a href={'https://open.spotify.com/artist/' + artist.id} className='ver_artist' target='_blank'>Ver artista</a>
                                                </div>
                                            </div>
                                        </div>
                                        <div className="col-4">
                                            <div className='generos'>
                                                <p>
                                                {artist.genres.map(
                                                    (genero,index) => {
                                                        return(
                                                            <span className='genero'>{genero} </span>
                                                        )
                                                    }
                                                )}
                                                </p>
                                            </div>
                                        </div>
                                    </div>
                                    <div className="row albums">
                                        { this.state.albums == true ?
                                        this.show_albums(this.state.albumnes) : ''}
                                    </div>
                                </div>
                            )
                        }) 
                    : ''}
                </main>
                <div className="list">

                </div>
            </div>
        )

    }
}




export default Main;
