import Axios from 'axios';
import React from 'react';

import '../css/main.scss'

class Main extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            artist : '',
            albums : false,
            content_albums : [],
            id : '',
            albumnes: ''
        }
    }
    albums(e){
        console.log(e.currentTarget)
        let temp_state = this
        this.setState({
            albums : true,
            id : e.currentTarget.id
        })
        let artist_id = e.currentTarget.id
        console.log(artist_id)
        console.log(this.props.token)

        Axios('https://api.spotify.com/v1/artists/' + artist_id + '/albums',{
            headers : {
                'Authorization' : 'Bearer ' + this.props.token
            },
            params : {
                id : artist_id
            },
            method: 'GET'
        })
        .then(function (response) {
            // handle success
            console.log(response)
            let albums_respuesta = response;
            albums_respuesta = JSON.stringify(albums_respuesta)

            temp_state.setState({
                albumnes : albums_respuesta
            })
            var albums = []
            albums = JSON.parse(albums_respuesta)
            albums = albums.data.items
        })
        .catch(function (error) {
            // handle success
            console.log(error)
        })
    }

    render(){
        let artists = ''
        if(this.props.found_data != ''){
            artists = JSON.parse(this.props.found_data)
            artists = artists.data.artists.items
        }
        return (
            <div>
                <main>
                    {artists ? artists.map(
                        (artist, key) => {
                            return(
                                <div>
                                    <div class='row content_artist' onClick={this.albums.bind(this)} id={artist.id}>
                                        <div className="col-2">
                                            <div className="cont_img">
                                                <img className='img-fluid' src={ artist.images[2] ? (artist.images[2].url) : ''} alt=""/>
                                            </div>
                                        </div>
                                        <div className="col-6">
                                            <div className="info_artist">
                                                <div>
                                                    <h3 class='artist'>{artist.name}</h3>
                                                    <p>followers {artist.followers.total}</p>
                                                    <a href={'https://open.spotify.com/artist/' + artist.id} className='ver_artist' target='_blank'>Ver artista</a>
                                                </div>
                                            </div>
                                        </div>
                                        <div className="col-4">
                                            <div className='generos'>
                                                <p>
                                                {artist.genres.map(
                                                    (genero,index) => {
                                                        return(
                                                            <span className='genero'>{genero} </span>
                                                        )
                                                    }
                                                )}
                                                </p>
                                            </div>
                                        </div>
                                    </div>
                                    <div className="row albums">
                                        {this.state.albumnes}
                                        {
                                            this.state.albums == true &&  this.state.id == artist.name ?
                                                albums.map(
                                                    album => {
                                                        return (
                                                            <div className="col-4">
                                                                <div className="album">
                                                                    <a href={album.external_urls.spotify} target='_blank'>
                                                                        <img src={album.images[1].url} class='img-fluid' alt=""/>
                                                                        <p>{album.name}</p>
                                                                    </a>
                                                                    <p class='tracks'>Tracks : <span>{album.total_tracks}</span></p>
                                                                    <span>{album.release_date}</span>
                                                                </div>
                                                            </div>
                                                        )
                                                    }
                                                )
                                            : ''
                                        }
                                    </div>
                                </div>
                            )
                        }) 
                    : ''}
                </main>
                <div className="list">

                </div>
            </div>
        )

    }
}




export default Main;
